import { useState } from "react";
import Sidebar from "./sidebar";
import { open, setopen } from "../App";
import { Link } from "react-router-dom";
import { useLocation } from "react-router-dom";
import jwt_decode from "jwt-decode";
import { useEffect } from "react";
import { Navigate, useNavigate } from "react-router-dom";

//

function navbar() {
  const toggleOffcanvas = () => {
    document
      .querySelectorAll(".sidebar-offcanvas")
      .forEach((el) => el.classList.toggle("open"));
  };

  return (
    <nav
      id="barrenavigation"
      className="navbar navbar-expand bg-white navbar-white sticky-top px-4 py-0"
    >
      <Link className="navbar-brand d-flex d-lg-none me-4">
        <h2 className="textprimary mb-0">
          <i className="fa fa-user-edit"></i>
        </h2>
      </Link>
      <button
        className="textprimary border-0 sidebar-toggler flex-shrink-0"
        onClick={() => toggleOffcanvas()}
      >
        <i className="fa fa-bars"></i>
      </button>
    </nav>
  );
}

export default navbar;
