import { Link } from "react-router-dom";
import React from "react";
import booksData from "../database.json";

export default function Sidebar() {
  const [books, setBooks] = React.useState([]);
  const [filteredBooks, setFilteredBooks] = React.useState([]);
  const [languages, setLanguages] = React.useState([]);
  const [activeLanguage, setActiveLanguage] = React.useState("All");

  React.useEffect(() => {
    // Charger les livres depuis le fichier JSON
    const allBooks = Object.keys(booksData).reduce((acc, lang) => {
      return [...acc, ...booksData[lang]];
    }, []);

    setBooks(allBooks);
    setFilteredBooks(allBooks); // Par défaut, tous les livres

    // Charger les langues à partir du JSON
    const availableLanguages = Object.keys(booksData);
    setLanguages(["All", ...availableLanguages]);
  }, []);

  const filterBooksByLanguage = (language) => {
    setActiveLanguage(language);
    if (language === "All") {
      setFilteredBooks(books); // Montrer tous les livres
    } else {
      setFilteredBooks(booksData[language]);
    }
  };
  return (
    <div id="sidebar" className="sidebar bg-white sidebar-offcanvas pe-2 pb-3">
      <nav className="navbar bg-white navbar-dark">
        <Link to={"/"} className="navbar-brand mx-4 mb-3">
          <h3 className="textprimary d-flex align-items-center me-5 mbr-section-subtitle align-center text-dark mbr-light mbr-fonts-style display-1 fs-1">
            Books
          </h3>
        </Link>

        <div className="navbar-nav w-100 bg-red">
          <ul className="list-group">
            <li
              className={`list-group-item ${
                activeLanguage === "All" ? "active" : ""
              }`}
            >
              <button
                className="btn btn-link p-0"
                onClick={() => filterBooksByLanguage("All")}
              >
                All Books
              </button>
            </li>
            {languages.slice(1).map((language, index) => (
              <li
                key={index}
                className={`list-group-item ${
                  activeLanguage === language ? "active" : ""
                }`}
              >
                <button
                  className="btn btn-link p-0"
                  onClick={() => filterBooksByLanguage(language)}
                >
                  {language}
                </button>
              </li>
            ))}
          </ul>
        </div>
      </nav>
    </div>
  );
}
