    if (language === "All") {
      setFilteredBooks(books); // Montrer tous les livres
    } else {
      setFilteredBooks(booksData[language]);
    }
  };
  return (
    <div id="sidebar" className="sidebar bg-white sidebar-offcanvas pe-2 pb-3">
      <nav className="navbar bg-white navbar-dark">
