import { Link } from "react-router-dom";
import { useState } from "react";

//

function Navbar(SelectedSearch) {
  const [text, setText] = useState("");

  const toggleOffcanvas = () => {
    document
      .querySelectorAll(".sidebar-offcanvas")
      .forEach((el) => el.classList.toggle("open"));
  };

  return (
    <nav
      id="barrenavigation"
      className="navbar navbar-expand bg-white navbar-white sticky-top px-4 py-0 gap-3"
    >
      {/* Bouton pour cacher la sidebar, ajout√© ici dans le contenu principal */}
      <button
        className="textprimary border-0 sidebar-toggler flex-shrink-0"
        onClick={() => toggleOffcanvas()}
      >
        toggle
      </button>{" "}
      <input
        type="text"
        placeholder="Enter Text..."
        className="form-control w-50 "
        value={text}
        onChange={(e) => setText(e.target.value)}
      />
      <button
        className="btn shadow-sm"
        onClick={() => {
          SelectedSearch(text);
        }}
      >
        Search
      </button>
    </nav>
  );
}

export default Navbar;
