      <h3>Transitions</h3>
      <ul>
        {automate.transitions.map((trans, idx) => (
          <li key={idx}>
            {trans.from} --({trans.symbol})--> {trans.to}
          </li>
        ))}
      </ul>
    </div>
  );
};

// Composant principal de l'application
const Automata = () => {
  const [automate, setAutomate] = useState(null);
  const [text, setText] = useState("");
  const [regex, setRegex] = useState("");
  const [occurrences, setOccurrences] = useState(0);

  const handleGenerateAutomate = () => {
    stateCounter = 0; // Réinitialiser le compteur d'état pour chaque nouveau calcul
    const automate = construireAutomate(regex);
    setAutomate(automate);
  };

  const handleCountOccurrences = () => {
    if (automate) {
      const count = compterOccurences(automate, text);
      setOccurrences(count);
    }
  };

  return (
    <div>
      <h1>Automate avec transitions epsilon</h1>
      <div>
        <label>
          Regex:
          <input
            required
            value={regex}
            onChange={(e) => setRegex(e.target.value)}
          />
        </label>
        {/* <button onClick={handleGenerateAutomate}>Générer l'automate</button> */}
      </div>
      <div>
        <label>
          Texte:
          <input
            required
            value={text}
            onChange={(e) => setText(e.target.value)}
          />
        </label>
        <button
          onClick={() => {
            handleGenerateAutomate();
            handleCountOccurrences();
          }}
        >
          Compter les occurrences
        </button>
      </div>
      {automate && <AutomateVisualizer automate={automate} />}
      <h3>Nombre d'occurrences: {occurrences}</h3>
    </div>
  );
};

export default Automata;
