// src/components/BookList.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import booksData from "../database.json";

const BookList = () => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    // Charger les livres depuis le fichier JSON
    const englishBooks = booksData.English;
    setBooks(englishBooks);
  }, []);

  const getDownloadLink = (link) => {
    const proxyUrl = "https://thingproxy.freeboard.io/fetch/";
    const gutenbergUrl = `https://www.gutenberg.org/cache/epub/${link}/pg${link}.txt`;
    return proxyUrl + gutenbergUrl;
  };

  const downloadBook = async (book) => {
    const downloadLink = getDownloadLink(book.link);

    try {
      // Faire une requête HTTP pour obtenir le fichier texte
      const response = await axios.get(downloadLink, {
        responseType: "blob",
      });

      // Créer une URL pour le fichier téléchargé
      const fileURL = window.URL.createObjectURL(new Blob([response.data]));
      const fileLink = document.createElement("a");
      fileLink.href = fileURL;
      fileLink.setAttribute("download", `${book.Title}.txt`);

      // Déclencher le téléchargement
      document.body.appendChild(fileLink);
      fileLink.click();
      document.body.removeChild(fileLink);
    } catch (error) {
      console.error("Erreur lors du téléchargement du livre:", error);
    }
  };

  return (
    <>
      <div className="card">
        <h1>Books in English</h1>
        <ul>
          {books.map((book, index) => (
            <li key={index}>
              <h2>{book.Title}</h2>
              <p>Author: {book.Author}</p>
              {/* Au lieu d'un simple lien, on appelle la fonction downloadBook */}
              <button
                onClick={() => {
                  console.log(book);
                  downloadBook(book);
                }}
              >
                Download TXT
              </button>
            </li>
          ))}
        </ul>
      </div>
    </>
  );
};

export default BookList;
