import React, { useEffect, useState } from "react";
import axios from "axios";
import booksData from "../database.json"; // Votre fichier JSON des livres

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [filteredBooks, setFilteredBooks] = useState([]);
  const [languages, setLanguages] = useState([]);
  const [activeLanguage, setActiveLanguage] = useState("All");
  const [isSidebarOpen, setIsSidebarOpen] = useState(true); // Nouvel état pour la sidebar

  useEffect(() => {
    // Charger les livres depuis le fichier JSON
    const allBooks = Object.keys(booksData).reduce((acc, lang) => {
      return [...acc, ...booksData[lang]];
    }, []);

    setBooks(allBooks);
    setFilteredBooks(allBooks); // Par défaut, tous les livres

    // Charger les langues à partir du JSON
    const availableLanguages = Object.keys(booksData);
    setLanguages(["All", ...availableLanguages]);
  }, []);

  const getDownloadLink = (link) => {
    const proxyUrl = "https://thingproxy.freeboard.io/fetch/";
    const gutenbergUrl = `https://www.gutenberg.org/cache/epub/${link}/pg${link}.txt`;
    return proxyUrl + gutenbergUrl;
  };

  const downloadBook = async (book) => {
    const downloadLink = getDownloadLink(book.link);

    try {
      const response = await axios.get(downloadLink, {
        responseType: "blob",
      });

      const fileURL = window.URL.createObjectURL(new Blob([response.data]));
      const fileLink = document.createElement("a");
      fileLink.href = fileURL;
      fileLink.setAttribute("download", `${book.Title}.txt`);

      document.body.appendChild(fileLink);
      fileLink.click();
      document.body.removeChild(fileLink);
    } catch (error) {
      console.error("Erreur lors du téléchargement du livre:", error);
    }
  };

  const filterBooksByLanguage = (language) => {
    setActiveLanguage(language);
    if (language === "All") {
      setFilteredBooks(books); // Montrer tous les livres
    } else {
      setFilteredBooks(booksData[language]);
    }
  };

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen); // Cacher ou montrer la sidebar
  };

  return (
    <div className="sidebar bg-white sidebar-offcanvas pe-2 pb-3">
      <div className="navbar bg-white navbar-dark">
        {/* Sidebar */}
        <div
          className={`sidebar bg-white sidebar-offcanvas p-3 ${
            isSidebarOpen ? "sidebar-open" : "sidebar-closed"
          }`}
          style={{
            minWidth: isSidebarOpen ? "250px" : "0px", // Ajuster la largeur selon l'état
            height: "100vh",
            transition: "all 0.3s ease",
            position: "fixed", // Fixer la sidebar
            top: 0,
            left: 0,
            zIndex: 1000,
          }}
        >
          <nav className="navbar bg-white ">
            <div className="sidebar-header mb-4 d-flex align-items-center justify-content-between">
              <a href="/" className="navbar-brand">
                <img
                  src="/path-to-logo.png"
                  alt="Logo"
                  style={{ width: "50px", height: "50px", marginRight: "10px" }}
                />
                <h3>My Book Site</h3>
              </a>
              {/* Bouton pour cacher la sidebar */}
              <button
                className="btn btn-outline-secondary"
                onClick={toggleSidebar}
              >
                {isSidebarOpen ? "Hide" : "Show"}
              </button>
            </div>
            <div className="navbar-nav w-100">
              <ul className="list-group">
                <li
                  className={`list-group-item ${
                    activeLanguage === "All" ? "active" : ""
                  }`}
                >
                  <button
                    className="btn btn-link p-0"
                    onClick={() => filterBooksByLanguage("All")}
                  >
                    All Books
                  </button>
                </li>
                {languages.slice(1).map((language, index) => (
                  <li
                    key={index}
                    className={`list-group-item ${
                      activeLanguage === language ? "active" : ""
                    }`}
                  >
                    <button
                      className="btn btn-link p-0"
                      onClick={() => filterBooksByLanguage(language)}
                    >
                      {language}
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          </nav>
        </div>
        <div class="content sidebar-offcanvas">
          {/* Main Content */}
          <div
            className="container-fluid p-4"
            style={{
              marginLeft: isSidebarOpen ? "250px" : "0px", // Ajuster la marge selon la visibilité de la sidebar
              transition: "all 0.3s ease",
              width: "100%",
            }}
          >
            <div className="row">
              {filteredBooks.map((book, index) => (
                <div key={index} className="col-12 col-sm-6 col-lg-4 mb-4">
                  <div className="card h-100" style={{ maxWidth: "18rem" }}>
                    <img
                      className="card-img-top"
                      src={`https://www.gutenberg.org/cache/epub/${book.link}/pg${book.link}.cover.medium.jpg`}
                      alt={book.Title}
                      style={{
                        width: "100%",
                        height: "250px",
                        objectFit: "cover",
                      }}
                    />
                    <div className="card-body">
                      <h5 className="card-title">{book.Title}</h5>
                      <p className="card-text">Author: {book.Author}</p>
                      <button
                        className="btn btn-primary"
                        onClick={() => downloadBook(book)}
                      >
                        Download TXT
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BookList;
