import { Link } from "react-router-dom";

//

function Navbar() {
  const toggleOffcanvas = () => {
    document
      .querySelectorAll(".sidebar-offcanvas")
      .forEach((el) => el.classList.toggle("open"));
  };

  return (
    <nav
      id="barrenavigation"
      className="navbar navbar-expand bg-white navbar-white sticky-top px-4 py-0"
    >
      <Link className="navbar-brand d-flex d-lg-none me-4">
        <h2 className="textprimary mb-0">
          <i className="fa fa-user-edit"></i>
        </h2>
      </Link>

      {/* Bouton pour cacher la sidebar, ajouté ici dans le contenu principal */}
      <button
        className="btn btn-primary mb-4"
        onClick={toggleOffcanvas}
        style={{
          position: "absolute",
          top: "20px",
          left: isSidebarOpen ? "260px" : "20px", // Se déplacer en fonction de l'état de la sidebar
          zIndex: 1100, // Au-dessus de tout
          transition: "left 0.3s ease", // Transition fluide
        }}
      >
        {isSidebarOpen ? "Hide Sidebar" : "Show Sidebar"}
      </button>
    </nav>
  );
}

export default Navbar;
