import { Link } from "react-router-dom";

//

function Navbar() {
  const toggleOffcanvas = () => {
    document
      .querySelectorAll(".sidebar-offcanvas")
      .forEach((el) => el.classList.toggle("open"));
  };

  return (
    <nav
      id="barrenavigation"
      className="navbar navbar-expand bg-white navbar-white sticky-top px-4 py-0"
    >
      {/* Bouton pour cacher la sidebar, ajout√© ici dans le contenu principal */}
      <button
        className="textprimary border-0 sidebar-toggler flex-shrink-0"
        onClick={() => toggleOffcanvas()}
      >
        toggle
      </button>{" "}
      <input type="text" placeholder="Enter Text..." className="form-control" />
    </nav>
  );
}

export default Navbar;
